import {AT_DEC, AT_DEL, AT_INC, increment} from "../actions";


const product = (state, action) => {
    switch (action.type) {
        case AT_INC:
            console.log(state);
            let newState = Object.assign({}, state);
            newState.count++;
            return newState;
        default:
            return state;
    }
}

const defState = [
    {id: 1, name: 'Велосипед', count: 5},
    {id: 2, name: 'Самокат', count: 3},
    {id: 3, name: 'Ролики', count: 7}
]

const storemanager = (state = defState, action) => {
    switch (action.type) {
        case AT_INC:
            console.log(action);
            let productIndex = getStateIndexByID(state, 2);
            let changedProduct = product(state[productIndex], action);
            let newState = Object.assign({}, state);
            newState[productIndex] = changedProduct;
            return newState;
        case AT_DEC:

            return state;
        case AT_DEL:

            return state;
        default:
            return state;
    }
}
const getStateIndexByID = (state, id) => {
    for (let i = 0; i < state.length; i++) {
        if (state[i].id === id) {
            return i;
        }
    }
    return -1;
}


export {product, storemanager}
