import {AT_DEC, AT_DEL, AT_INC, increment} from "../actions";

const product = (state, action) => {
    switch (action.type) {
        case AT_INC:
            console.log(state);
            return state
        default:
            return state
    }
}

const defState = [
    {id: 1, name: 'Велосипед', count: 5},
    {id: 2, name: 'Самокат', count: 3},
    {id: 3, name: 'Ролики', count: 7}
]

const storemanager = (state = defState, action) => {
    switch (action.type) {
        case AT_INC:
            console.log(action);
            product(state[getStateIndexByID(2)], action);
            // state[2].count++;
            return state;
        case AT_DEC:
            // state[2].count--;
            return state;
        case AT_DEL:
            // delete state[2];
            return state;
        default:
            return state;
    }
}
const getStateIndexByID = (state, id) => {
    for (let i = 0; i < state.length; i++) {
        if (state[i].id === id) {
            return i;
        }
    }
    return -1;
}


export {product, storemanager}
