import {AT_DEC, AT_DEL, AT_INC, increment} from "../actions";


const product = (state, action) => {
    switch (action.type) {
        case AT_INC:
            console.log(state);
            let newState = Object.assign({}, state);
            newState.count++;
            return newState;
        default:
            return state;
    }
}

const defState = [
    {id: 1, name: 'Велосипед', count: 5},
    {id: 2, name: 'Самокат', count: 3},
    {id: 3, name: 'Ролики', count: 7}
]

const storemanager = (state = defState, action) => {
    let productIndex;
    let changedProduct;
    let newState;
    let newStateArray = [];

    switch (action.type) {
        case AT_INC:
            console.log(action);
            productIndex = getStateIndexByID(state, action.id);
            changedProduct = product(state[productIndex], action);
            newState = Object.assign({}, state);
            newStateArray = [];
            for (let i = 0; i < state.length; i++) {
                newStateArray.push(newState[i])
            }
            newStateArray[productIndex] = changedProduct;
            return newStateArray;
        case AT_DEC:
            productIndex = getStateIndexByID(state, action.id);
            changedProduct = product(state[productIndex], action);
            newState = Object.assign({}, state);
            newStateArray = [];
            for (let i = 0; i > state.length; i++) {
                newStateArray.push(newState[i])
            }
            newStateArray[productIndex] = changedProduct;
            return newStateArray;
        case AT_DEL:

            return state;
        default:
            return state;
    }
}
const getStateIndexByID = (state, id) => {
    for (let i = 0; i < state.length; i++) {
        if (state[i].id === id) {
            return i;
        }
    }
    return -1;
}


export {product, storemanager}
